import React, { useState } from 'react';
import Card from '../UI/Card';
 import classes from "./AvailableMeals.module.css"
import MealItem from './MealItem';




const DUMMY_MEALS = [
    {
      id: 'm1',
      name: 'Sushi',
      description: 'Finest fish and veggies',
      price: 22.99,
    },
    {
      id: 'm2',
      name: 'Schnitzel',
      description: 'A german specialty!',
      price: 16.5,
    },
    {
      id: 'm3',
      name: 'Barbecue Burger',
      description: 'American, raw, meaty',
      price: 12.99,
    },
    {
      id: 'm4',
      name: 'Green Bowl',
      description: 'Healthy...and green...',
      price: 18.99,
    },
  ];


const AvailableMeals = () =>{

  const [meals, setMeals] = useState({})


  function fetchMeals(){
    fetch("https://buddha-fc14c-default-rtdb.firebaseio.com/").then(convert => {
      return convert.json
    }).then(data =>{
    setMeals(data.meals)
    })
    }



    const food = DUMMY_MEALS.map(meal => <MealItem key ={meal.id} id={meal.id} name ={meal.name} description = {meal.description} price = {meal.price}  />)
return(
    <section className={classes.meals}>

    <Card>
    <ul >{food}</ul>
       </Card>
    </section>
)
}

export default AvailableMeals
