import React, {useReducer} from 'react';
import CartContext from './cartContext';



/** 
const cartReducer = (state, action) =>{
    if(action.type === "ADD"){

        const updatedTotalAmount = state.totalAmount + action.item.price * action.item.amount

const cartItemIndex = state.items.findIndex(item => item.id === action.item.id)
const exsistingItem = state.items[cartItemIndex]


let updatedItemss;

if(exsistingItem){

   const updatedItem = {
        ...exsistingItem,
        amount:exsistingItem.amount + action.item.amount
    }
    updatedItemss = [...state.items]
    updatedItemss[exsistingItem] = updatedItem
}else{

    updatedItemss = state.items.concat(action.item)
}


  const updateItems = state.items.concat(action.item)
  const updatedTotalAmount = state.totalAmount + action.item.type * action.item.amount
return{
    item:updateItems,
    totalAmount:updatedTotalAmount
}
    }
    return defaultCartState
}
