import React, { useContext } from 'react';
import Modal from '../UI/Modal';
import classes from "./Cart.module.css"
import CartContext from '../../store/cartContext';

const Cart = (props) =>{

const cartContext = useContext(CartContext)

const cartItems=<ul className={classes["cart-items"]} > {cartContext.items.map(item => <li key={item.id}>{item.name} </li>)}</ul>

const totalAmount = `$${cartContext.totalAmount.toFixed(2)}`
const hasItems = cartContext.items.length > 0

return (<Modal onHideModal = {props.onHideModal}>
{cartItems}
<div className= {classes.total}>
    <span>Total amount</span>
    <span>{totalAmount}</span>
</div>
<div className={classes.actions}>
<button className={classes["button--alt"]} onClick={props.onHideModal} >Close</button>
{<button className={classes.button} >Order</button>}

</div>

</Modal>)
}

export default Cart
